				//do nothing
				if (response.data.identityType && response.data.identityType != 'C') {
					$rootScope.userType = 'notAcustomer';
				}
				if (response.data.identityType && response.data.identityType != 'X') {
					$rootScope.authorized = true;
				}
				console.log(response)
